// Generated by CoffeeScript 1.6.3
(function() {
  var adjustLayout, templates;

  templates = {
    column_markup: '<div class="column" data-number="<%= number+1 %>"></div>'
  };

  adjustLayout = function(val) {
    var $layout, i, _results;
    $layout = $('#layout');
    $layout.find('.column').remove();
    i = 0;
    _results = [];
    while (i < val) {
      $layout.append(_.template(templates.column_markup, {
        'number': i
      }));
      _results.push(i++);
    }
    return _results;
  };

  $(function() {
    adjustLayout($('#columns').val());
    $('#pllrs').on('keyup', function(e) {
      return $('#layout').attr('class', e.currentTarget.value);
    });
    $('.favorite').on('click', function(e) {
      var new_class;
      e.preventDefault();
      new_class = $(this).data('class');
      $('#pllrs').val(new_class);
      return $('#layout').attr('class', new_class);
    });
    return $('#columns').on('keyup', function(e) {
      return adjustLayout(e.currentTarget.value);
    });
  });

}).call(this);
